  selector: 'app-root',
  template: `
    <app-toast/>

    <router-outlet></router-outlet>
  `,
  styles: []
})
export class AppComponent {
  title = 'splitExpense';
  ngOnInit(): void {
    //Called after the constructor, initializing input properties, and the first call to ngOnChanges.
    //Add 'implements OnInit' to the class.
